(()=>{"use strict";const e=window.wp.hooks;(0,e.addAction)("launchpadBlocks.carousel.registerFunctionality","launchpadBlocks.carousel.dots",s=>{const t=s.carousel.querySelector("div[data-carousel-dots]");if(t){const o=t.querySelectorAll("button[data-carousel-slide]");if(o.length<0)throw new Error("Unable to find the dots in your dots container.");for(const e of o){const t=e.dataset.carouselSlide;if(!t||Number(t)>s.slideCount||Number(t)<0)throw new Error("Invalid slide number on dot.");e.addEventListener("click",()=>{s.goToSlide(Number(t))})}(0,e.addAction)("launchpadBlocks.carousel.updateActiveSlide","launchpadBlocks.carousel.dots",()=>{var e;(e=[...o].find(e=>e.classList.contains("is-selected")))&&e.classList.remove("is-selected");const t=[...o].find(e=>Number(e.dataset.carouselSlide)===s.currentSlide);if(!t)throw new Error("Unable to find the selected slide's thumbnail.");t.classList.add("is-selected")})}})})();